import styled, { createGlobalStyle, ThemeProvider } from "styled-components";
import React, { useEffect, useRef, useState } from "react";
import {
  motion,
  useMotionValue,
  useTransform,
  useViewportScroll,
  AnimatePresence,
} from "framer-motion";

const Wrapper = styled(motion.div)`
  height: 100vh;
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
  background: linear-gradient(
    135deg,
    rgba(0, 186, 238, 0.78),
    rgb(221, 0, 238)
  );
`;

const Box = styled(motion.div)`
  width: 200px;
  height: 200px;
  background-color: whitesmoke;
  border-radius: 15px;
  display: flex;
  font-size: 28px;
  justify-content: center;
  align-items: center;
  position: absolute;
  top: 100px;
`;
const BiggerBox = styled.div`
  width: 600px;
  height: 600px;
  background-color: rgba(255, 255, 255, 0.4);
  border-radius: 35px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Circle = styled(motion.div)`
  background-color: white;
  place-self: center;
  height: 70px;
  width: 70px;
  border-radius: 35px;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1), 0 10px 20px rgba(0, 0, 0, 0.06);
`;
const boxVariants = {};

function App() {
  return (
    <Wrapper>
      <Box />
    </Wrapper>
  );
}

export default App;
