import styled, { createGlobalStyle, ThemeProvider } from "styled-components";
import React, { useEffect, useRef } from "react";
import {
  motion,
  useMotionValue,
  useTransform,
  useViewportScroll,
} from "framer-motion";

const Wrapper = styled(motion.div)`
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(
    135deg,
    rgba(0, 186, 238, 0.78),
    rgb(221, 0, 238)
  );
`;

const Box = styled(motion.div)`
  width: 200px;
  height: 200px;
  background-color: whitesmoke;
  border-radius: 15px;
`;
const BiggerBox = styled.div`
  width: 600px;
  height: 600px;
  background-color: rgba(255, 255, 255, 0.4);
  border-radius: 35px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`;

const Circle = styled(motion.div)`
  background-color: white;
  place-self: center;
  height: 70px;
  width: 70px;
  border-radius: 35px;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1), 0 10px 20px rgba(0, 0, 0, 0.06);
`;
const boxVariants = {
  start: {},
  end: {},
  click: { scale: 1, borderRadius: "100px" },
  drag: {},
};

function App() {
  const x = useMotionValue(0);
  const ts = useTransform(x, [-800, 800], [-360, 360]);
  const gradient = useTransform(
    x,
    [-800, 0, 800],
    [
      "linear-gradient(135deg,rgba(0, 28, 238, 0.78),rgb(0, 182, 238))",
      "linear-gradient(135deg,rgba(0, 186, 238, 0.78),rgb(221, 0, 238))",
      "linear-gradient(135deg,rgba(238, 0, 226, 0.78),rgb(238, 0, 48))",
    ]
  );
  const { scrollY, scrollYProgress } = useViewportScroll();
  useEffect(() => {
    scrollY.onChange(() => {
      console.log(scrollY.get(), scrollYProgress.get());
    });
  }, [scrollY, scrollYProgress]);
  return (
    <Wrapper style={{ background: gradient }}>
      <Box style={{ x, rotateZ: ts }} drag="x" dragSnapToOrigin />
    </Wrapper>
  );
}

export default App;
