  //   const {
  //     currentTarget: { value },
  //   } = event;
  //   setValue(value);
  // }
  // function onSubmit(event: React.FormEvent<HTMLFormElement>) {
  //   event.preventDefault();
  //   console.log(event);
  //   setValue("");
  // }
  // return (
  //   <div>
  //     <form onSubmit={onSubmit}>
  //       <input
  //         value={value}
  //         type={"text"}
  //         placeholder={"username"}
  //         onChange={onChange}
  //       />
  //       <button>Login</button>
  //     </form>
  //   </div>
  // );
}

export default App;
